--- Gets Icons
---@param ft string?
---@return { name: string, sign: string, sign_hl: string, icon: string, icon_hl: string, border_hl: string }
fts.get = function (ft)
	local _ft = ft;

	if vim.filetype.match({ filename = string.format("example.%s", _ft) }) then
		_ft = vim.filetype.match({ filename = string.format("example.%s", _ft) });
	end

	local spec = require("markview.spec");
	local provider_name = spec.get({ "preview", "icon_provider" }, { fallback = "internal", ignore_enable = true });
	local conf = {};

	if provider_name == "devicons" and  package.loaded["nvim-web-devicons"] then
		conf.icon, conf.icon_hl = package.loaded["nvim-web-devicons"].get_icon_by_filetype(
			_ft,
			{ default = true }
		);

		conf.icon = conf.icon .. " ";

		conf.sign = conf.icon;
		conf.sign_hl = conf.icon_hl;
		conf.border_hl = conf.icon_hl;
	elseif provider_name == "mini" and package.loaded["mini.icons"] then
		--- Attempt to get icon from filetype directly.
		---@type string, string, boolean
		local ft_icon, ft_icon_hl, is_default = package.loaded["mini.icons"].get(
			"filetype",
			_ft
		);

		if is_default == true then
			--- There is no icon for the filetype.
			--- Attempt to get the icon from file path instead.

			conf.icon, conf.icon_hl = package.loaded["mini.icons"].get(
				"file",
				string.format("example.%s", ft)
			);
		else
			conf.icon = ft_icon;
			conf.icon_hl = ft_icon_hl;
		end

		conf.icon = conf.icon .. " ";

		conf.sign = conf.icon;
		conf.sign_hl = conf.icon_hl;
		conf.border_hl = conf.icon_hl;
	elseif provider_name == "" then
		local no_icon = "";

		conf.icon = no_icon;
		conf.icon_hl = "MarkviewIcon0";

		conf.sign = no_icon;
		conf.hl = no_icon;
	else
		conf = vim.tbl_extend("force", fts.styles[_ft] or fts.styles[ft] or fts.styles["default"], {});
	end

	local this_conf = fts.styles[_ft] or fts.styles[ft] or fts.styles.default;
	conf.name = this_conf.name or string.gsub(ft, "^%w", string.upper) or "Unknown";

	return conf;
end

return fts;
